import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import AdminDashboard from "./components/AdminDashboard/AdminDashboard"; // Ensure this path is correct
import Question from "./components/UserDashboard/Question"; // Ensure this path is correct
import CandidateDashboard from "./components/UserDashboard/CandidateDashboard";
import MySimulations from './components/UserDashboard/MySimulations';


function InterviewPage() {
  const [question, setQuestion] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userAnswer, setUserAnswer] = useState("");
  const [submitMessage, setSubmitMessage] = useState("");
  const [currentQuestionId, setCurrentQuestionId] = useState(null);
  const [timeLeft, setTimeLeft] = useState(10 * 60); // 10 minutes in seconds
  const [interviewComplete, setInterviewComplete] = useState(false);

  // Fetch the first question or the next question
  const fetchQuestion = async (id = null) => {
    try {
      setLoading(true);
      const url = id
        ? `http://127.0.0.1:8000/api/fetch-next-question/?current_question_id=${id}`
        : "http://127.0.0.1:8000/api/fetch-next-question/";
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }
      const data = await response.json();
      if (data.message === "No more questions available.") {
        setQuestion(null);
        setSubmitMessage("Interview completed! Thank you.");
        setInterviewComplete(true);
      } else {
        setQuestion(data);
        setCurrentQuestionId(data.id);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchQuestion();

    // Start the timer
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer); // Clear timer when it reaches 0
          setInterviewComplete(true);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://127.0.0.1:8000/api/submit-response/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          question_id: currentQuestionId,
          user_answer: userAnswer,
        }),
      });

      const data = await response.json();
      if (response.ok) {
        setSubmitMessage(data.message);
        setUserAnswer("");
        fetchQuestion(currentQuestionId); // Fetch the next question
      } else {
        setSubmitMessage(data.error);
      }
    } catch (err) {
      setSubmitMessage("An error occurred. Please try again.");
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs < 10 ? "0" : ""}${secs}`;
  };

  if (interviewComplete) {
    return (
      <div style={{ textAlign: "center", padding: "20px" }}>
        <h1>Interview Completed</h1>
        <p>Thank you for participating in the interview!</p>
      </div>
    );
  }

  if (loading) {
    return <h1>Loading...</h1>;
  }

  if (error) {
    return <h1>Error: {error}</h1>;
  }

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>Interview Question</h1>
      <h2>Time Left: {formatTime(timeLeft)}</h2>
      {question && (
        <div>
          <h2>{question.question}</h2>
          <p>
            <strong>Difficulty:</strong> {question.difficulty}
          </p>
          <form onSubmit={handleSubmit}>
            <label>
              Your Answer:
              <input
                type="text"
                value={userAnswer}
                onChange={(e) => setUserAnswer(e.target.value)}
                required
              />
            </label>
            <br />
            <button type="submit">Submit</button>
          </form>
          {submitMessage && <p>{submitMessage}</p>}
        </div>
      )}
      {!question && <h2>{submitMessage}</h2>}
    </div>
  );
}

function App(){
  return (
    <Router>
      <Routes>
        <Route path="/" element={<InterviewPage />} />
        <Route path="/admin-dashboard" element={<AdminDashboard />} />
        <Route path="/CandidateDashboard" element={<CandidateDashboard />} />
        <Route path="/MySimulations" element={<MySimulations />} />
      </Routes>
    </Router>
  );
}

export default App;
