import React, { useState } from "react";
import axios from "axios";
import "./Styles/UserSignup.css";

const UserSignup = () => {
    const [formData, setFormData] = useState({
        firstName: "",
        lastName: "",
        mobileNumber: "",
        email: "",
        password: "",
        confirmPassword: "",
    });
    const [photo, setPhoto] = useState(null);
    const [error, setError] = useState("");
    const [success, setSuccess] = useState("");

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFileChange = (e) => {
        setPhoto(e.target.files[0]);
    };

    // const handleSubmit = async (e) => {
    //     e.preventDefault();
    //     setError("");
    //     setSuccess("");

    //     if (formData.password !== formData.confirmPassword) {
    //         setError("Passwords do not match!");
    //         return;
    //     }

    //     const form = new FormData();
    //     form.append("data", JSON.stringify(formData));
    //     if (photo) {
    //         form.append("photo", photo);
    //     }

    //     try {
    //         const response = await axios.post("http://localhost:8000/api/signup/", form, {
    //             headers: { "Content-Type": "multipart/form-data" },
    //         });
    //         if (response.status === 201) {
    //             setSuccess("User registered successfully!");
    //             setFormData({
    //                 firstName: "",
    //                 lastName: "",
    //                 mobileNumber: "",
    //                 email: "",
    //                 password: "",
    //                 confirmPassword: "",
    //             });
    //             setPhoto(null);
    //         }
    //     } catch (err) {
    //         setError(err.response?.data?.error || "Something went wrong.");
    //     }
    // };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");
        setSuccess("");
    
        if (formData.password !== formData.confirmPassword) {
            setError("Passwords do not match!");
            return;
        }
    
        const form = new FormData();
        form.append("firstName", formData.firstName);
        form.append("lastName", formData.lastName);
        form.append("mobileNumber", formData.mobileNumber);
        form.append("email", formData.email);
        form.append("password", formData.password);
        if (photo) {
            form.append("photo", photo);  // Append the file
        }
    
        try {
            const response = await axios.post("http://localhost:8000/signup/", form, {
                headers: { "Content-Type": "multipart/form-data" },
            });
            if (response.status === 201) {
                setSuccess("User registered successfully!");
                setFormData({
                    firstName: "",
                    lastName: "",
                    mobileNumber: "",
                    email: "",
                    password: "",
                    confirmPassword: "",
                });
                setPhoto(null);
            }
        } catch (err) {
            setError(err.response?.data?.error || "Something went wrong.");
        }
    };
    

    return (
        <div className="signup-container">
            <h2>User Signup</h2>
            {error && <p className="error-message">{error}</p>}
            {success && <p className="success-message">{success}</p>}
            <form onSubmit={handleSubmit} className="signup-form">
                <input
                    type="text"
                    name="firstName"
                    placeholder="First Name"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="lastName"
                    placeholder="Last Name"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="mobileNumber"
                    placeholder="Mobile Number"
                    value={formData.mobileNumber}
                    onChange={handleChange}
                    required
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                />
                <input
                    type="password"
                    name="confirmPassword"
                    placeholder="Confirm Password"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                    required
                />
                <input type="file" name="photo" onChange={handleFileChange} placeholder/>
                <button type="submit">Sign Up</button>
            </form>
        </div>
    );
};

export default UserSignup;
