import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

function InterviewPage() {
    const [question, setQuestion] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [userAnswer, setUserAnswer] = useState("");
    const [submitMessage, setSubmitMessage] = useState("");
    const [evaluationResult, setEvaluationResult] = useState(null);
    const [currentQuestionId, setCurrentQuestionId] = useState(null);

    const fetchQuestion = async () => {
        try {
            setLoading(true);
            const response = await axios.get("http://127.0.0.1:8000/api/fetch-next-question/");
            if (response.data) {
                setQuestion(response.data);
                setCurrentQuestionId(response.data.id);
            }
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchQuestion();
    }, []);

    const handleSubmit = async () => {
        if (!userAnswer.trim()) {
            alert("Please provide an answer before submitting.");
            return;
        }

        try {
            const response = await axios.post("http://127.0.0.1:8000/api/evaluate-answer/", {
                question_id: currentQuestionId,
                question: question.question,
                expected_answer: question.answer,
                user_answer: userAnswer,
                difficulty: question.difficulty,
            });

            if (response.status === 200) {
                setEvaluationResult(response.data);
                setSubmitMessage("Answer evaluated successfully!");
                setUserAnswer(""); // Clear user answer
            } else {
                setSubmitMessage("An error occurred while evaluating the answer.");
            }
        } catch (err) {
            setSubmitMessage("A network error occurred. Please try again.");
        }
    };

    if (loading) return <h1>Loading...</h1>;
    if (error) return <h1>Error: {error}</h1>;

    return (
        <div style={{ textAlign: "center", padding: "20px" }}>
            <h1>Interview Question</h1>
            {question && (
                <div>
                    <h2>{question.question}</h2>
                    <textarea
                        value={userAnswer}
                        onChange={(e) => setUserAnswer(e.target.value)}
                        rows="4"
                        cols="50"
                    />
                    <br />
                    <button onClick={handleSubmit}>Submit Answer</button>
                </div>
            )}
            {submitMessage && <p>{submitMessage}</p>}
            {evaluationResult && (
                <div>
                    <h3>Evaluation Results</h3>
                    <p>Technical Score: {evaluationResult.technical_score}</p>
                    <p>Semantic Score: {evaluationResult.semantic_score}</p>
                    <p>Completeness Score: {evaluationResult.completeness_score}</p>
                    <p>Final Score: {evaluationResult.final_score}</p>
                </div>
            )}
        </div>
    );
}

export default InterviewPage;
