import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"; // For navigation
import axios from "axios";
import CompatibilityTest from "./CompatibilityTest"; // Import the CompatibilityTest component

const UserDashboard = () => {
    const [jobs, setJobs] = useState([]); // To store job posts
    const [loading, setLoading] = useState(true); // Loading state
    const [showTest, setShowTest] = useState(false); // To toggle the compatibility test view
    const [selectedJobId, setSelectedJobId] = useState(null); // To store the selected job ID
    const navigate = useNavigate(); // React Router hook for navigation

    // Fetch job posts from the API
    useEffect(() => {
        const fetchJobs = async () => {
            try {
                const response = await axios.get("http://localhost:8000/api/jobs/");
                setJobs(response.data);
            } catch (error) {
                console.error("Error fetching job posts:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchJobs();
    }, []);

    // Handle "Take Interview" button click
    const handleTakeInterview = (jobId) => {
        setSelectedJobId(jobId); // Store the selected job ID
        setShowTest(true); // Show the compatibility test
    };

    // Handle completion of the compatibility test
    const handleTestComplete = () => {
        // Redirect to the interview page with the selected job ID
        navigate(`/Interviewpage/${selectedJobId}`);
    };

    return (
        <div className="dashboard-container">
            {showTest ? (
                // Show the CompatibilityTest component if showTest is true
                <CompatibilityTest onTestComplete={handleTestComplete} />
            ) : (
                <div>
                    <div className="sidebar">
                        <h2>User Dashboard</h2>
                        <ul>
                            <li><a href="#">Dashboard</a></li>
                            <li><a href="#">Profile</a></li>
                            <li><a href="userlogin">Logout</a></li>
                        </ul>
                    </div>
                    <div className="main-content">
                        <div className="header">
                            <h1>Welcome to the Job Portal</h1>
                        </div>
                        <div className="jobs">
                            <h3>Available Jobs</h3>
                            {loading ? (
                                <p>Loading jobs...</p>
                            ) : jobs.length === 0 ? (
                                <p>No jobs available at the moment.</p>
                            ) : (
                                <ul>
                                    {jobs.map((job) => (
                                        <li key={job.id} className="job-card">
                                            <h4>{job.title}</h4>
                                            <p>{job.description}</p>
                                            <span>
                                                Posted on: {new Date(job.created_at).toLocaleDateString()}
                                            </span>
                                            <br />
                                            <button
                                                className="take-interview-btn"
                                                onClick={() => handleTakeInterview(job.id)}
                                            >
                                                Take Interview
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default UserDashboard;
