import * as faceapi from 'face-api.js';

// Function to load models
export const loadModels = async () => {
    try {
        const MODEL_URL = '/models'; // Path where models are stored
        await faceapi.nets.ssdMobilenetv1.loadFromUri(MODEL_URL);
        await faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL);
        await faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL);
        console.log("Models loaded successfully.");
        return true; // Return true when models are loaded
    } catch (error) {
        console.error("Error loading models:", error);
        return false; // Return false if loading models fails
    }
};

// Function to load the initial image for face detection
export const loadInitialImage = async (isModelLoaded) => {
    if (!isModelLoaded) {
        console.error("Models not loaded. Cannot perform face detection.");
        return;
    }

    try {
        const img = await faceapi.fetchImage('/images/initialImage.jpg'); // Use image from the public folder
        const detections = await faceapi.detectSingleFace(img).withFaceLandmarks().withFaceDescriptor();
        if (detections) {
            console.log("Initial image processed. Face detected.");
        } else {
            console.error("No face detected in the initial image.");
        }
    } catch (error) {
        console.error("Error loading or processing initial image:", error);
    }
};
