import React, { useState } from "react";
import { ReactMic } from "react-mic";
import Webcam from "react-webcam";
import { useNavigate } from "react-router-dom"; 

const CompatibilityTest = ({ onTestComplete }) => {
    const [voiceTestDone, setVoiceTestDone] = useState(false);
    const [videoTestDone, setVideoTestDone] = useState(false);
    const [internetSpeed, setInternetSpeed] = useState(null);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    const handleVoiceTest = (recordedBlob) => {
        console.log("Recorded Blob:", recordedBlob);
        setVoiceTestDone(true);
    };

    const handleInternetTest = async () => {
        setLoading(true);
        try {
            const response = await fetch("https://www.google.com", { mode: "no-cors" });
            if (response.ok || response.type === "opaque") {
                alert("Internet test passed!");
                // Redirect to the Interview page after the test passes
                navigate(`/Interviewpage/${jobId}`); // Navigate to the Interview page with the jobId
            } else {
                alert("Internet test failed.");
            }
        } catch (error) {
            console.error("Internet test error:", error);
            alert("Internet test failed: Unable to fetch the URL.");
        } finally {
            setLoading(false);
        }
    };
      

    return (
        <div>
            <h1>Compatibility Test</h1>
            {/* Voice Test */}
            {!voiceTestDone && (
                <div>
                    <h2>Voice Test</h2>
                    <ReactMic
                        record={true}
                        onStop={handleVoiceTest}
                        mimeType="audio/wav"
                    />
                    <button onClick={() => setVoiceTestDone(true)}>Skip</button>
                </div>
            )}

            {/* Video Test */}
            {voiceTestDone && !videoTestDone && (
                <div>
                    <h2>Video Test</h2>
                    <Webcam />
                    <button onClick={() => setVideoTestDone(true)}>Confirm</button>
                </div>
            )}

            {/* Internet Test */}
            {videoTestDone && internetSpeed === null && (
                <div>
                    <h2>Internet Test</h2>
                    <button onClick={handleInternetTest}>Check Internet Speed</button>
                </div>
            )}

            {/* Summary */}
            {internetSpeed !== null && (
                <div>
                    <h2>Test Summary</h2>
                    <p>Voice Test: {voiceTestDone ? "Passed" : "Skipped"}</p>
                    <p>Video Test: {videoTestDone ? "Passed" : "Skipped"}</p>
                    <p>Internet Speed: {internetSpeed} Mbps</p>
                    <button onClick={onTestComplete}>Proceed to Interview</button>
                </div>
            )}
        </div>
    );
};

export default CompatibilityTest;
