import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

function InterviewPage() {
    const [question, setQuestion] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [userAnswer, setUserAnswer] = useState("");
    const [submitMessage, setSubmitMessage] = useState("");

    const fetchQuestion = async (id = null) => {
        try {
            setLoading(true);
            const url = id
                ? `http://127.0.0.1:8000/api/fetch-next-question/?current_question_id=${id}`
                : "http://127.0.0.1:8000/api/fetch-next-question/";
            const response = await axios.get(url);
            if (response.data.message === "No more questions available.") {
                setQuestion(null);
                setSubmitMessage("Interview completed! Thank you.");
            } else {
                setQuestion(response.data);
            }
        } catch (err) {
            setError("Error fetching question.");
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async () => {
        try {
            const response = await axios.post("http://127.0.0.1:8000/api/submit-response/", {
                question_id: question.id,
                user_answer: userAnswer,
            });
            setSubmitMessage(response.data.message);
            fetchQuestion(question.id); // Fetch next question
            setUserAnswer(""); // Reset answer field
        } catch (err) {
            setError("Error submitting response.");
        }
    };

    useEffect(() => {
        fetchQuestion(); // Fetch first question on load
    }, []);

    if (loading) return <h1>Loading...</h1>;
    if (error) return <h1>{error}</h1>;

    return (
        <div>
            <h1>Interview Question</h1>
            {question ? (
                <div>
                    <h2>{question.text}</h2>
                    <p><strong>Difficulty:</strong> {question.difficulty}</p>
                    <textarea
                        value={userAnswer}
                        onChange={(e) => setUserAnswer(e.target.value)}
                        rows="4"
                        cols="50"
                    />
                    <button onClick={handleSubmit}>Submit Answer</button>
                </div>
            ) : (
                <p>{submitMessage}</p>
            )}
        </div>
    );
}

export default InterviewPage;
