import React, { useState } from "react";
import axios from "axios";

const ResumeUploadPage = ({ onResumeUploaded }) => {
    const [resumeFile, setResumeFile] = useState(null);
    const [uploadMessage, setUploadMessage] = useState("");

    const handleFileChange = (event) => {
        setResumeFile(event.target.files[0]);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!resumeFile) {
            alert("Please upload a resume.");
            return;
        }

        const formData = new FormData();
        formData.append("resume", resumeFile);

        try {
            const response = await axios.post("http://127.0.0.1:8000/api/upload-resume/", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });

            if (response.status === 200) {
                setUploadMessage("Resume uploaded successfully!");
                onResumeUploaded(response.data); // Pass extracted data to parent component if needed
            } else {
                setUploadMessage("Failed to upload resume.");
            }
        } catch (error) {
            console.error("Error uploading resume:", error);
            setUploadMessage("An error occurred while uploading the resume.");
        }
    };

    return (
        <div className="resume-upload-page">
            <h2>Upload Your Resume</h2>
            <form onSubmit={handleSubmit}>
                <input type="file" accept=".pdf,.doc,.docx" onChange={handleFileChange} />
                <button type="submit">Upload Resume</button>
            </form>
            {uploadMessage && <p>{uploadMessage}</p>}
        </div>
    );
};

export default ResumeUploadPage;
