import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // Import navigation hook

const UserDashboard = () => {
    const [jobs, setJobs] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate(); // Initialize navigation hook

    useEffect(() => {
        // Fetch jobs from the backend
        const fetchJobs = async () => {
            try {
                const response = await axios.get("http://localhost:8000/api/jobs/");
                setJobs(response.data);
            } catch (error) {
                console.error("Error fetching job posts:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchJobs();
    }, []);

    // Function to navigate to InterviewPage
    const handleTakeInterview = () => {
        navigate("/Interviewpage"); // Redirect to InterviewPage
    };

    return (
        <div className="dashboard-container">
            <div className="sidebar">
                <h2>User Dashboard</h2>
                <ul>
                    <li><a href="#">Dashboard</a></li>
                    <li><a href="#">Profile</a></li>
                    <li><a href="userlogin">Logout</a></li>
                </ul>
            </div>
            <div className="main-content">
                <div className="header">
                    <h1>Welcome to the Job Portal</h1>
                </div>
                <div className="jobs">
                    <h3>Available Jobs</h3>
                    {loading ? (
                        <p>Loading jobs...</p>
                    ) : jobs.length === 0 ? (
                        <p>No jobs available at the moment.</p>
                    ) : (
                        <ul>
                            {jobs.map((job) => (
                                <li key={job.id} className="job-card">
                                    <h4>{job.title}</h4>
                                    <p>{job.description}</p>
                                    <span>Posted on: {new Date(job.created_at).toLocaleDateString()}</span>
                                </li>
                            ))}
                            <div className="interview-button-container">
                    <button className="take-interview-button" onClick={handleTakeInterview}>
                        Take Interview
                    </button>
                </div>
                        </ul>
                    )}
                </div>

            </div>
        </div>
    );
};

export default UserDashboard;
